mapper.select:
select m.`id`, m.`qr_code`, m.`material_name`, m.`location`, m.region_id, m.enterprise_id, m.`material_type_id`, m.check_specific_id, m.`produced_date`, m.`expire_date`, m.`check_date`, m.`material_status`, m.material_url, m.remark, m.`create_time`, m.`update_time`, m.`creator`, m.`creator_id`, m.`updator`, m.`updator_id`, t.name AS materialTypeName from material AS m LEFT JOIN material_type AS t ON m.material_type_id = t.id WHERE 1=1
AND m.material_name LIKE concat('%',#{materialName},'%')
AND m.region_id IN
#{regionId}
AND m.enterprise_id IN
#{enterpriseId}
AND m.enterprise_id = #{enterpriseId}
AND m.`material_status` = #{materialStatus}
order by ${sidx} ${order}
order by id desc
limit #{offset}, #{limit}

select count(1) from material AS m
LEFT JOIN material_type AS t ON  m.material_type_id = t.id
WHERE 1=1

	AND m.material_name LIKE concat('%',#{materialName},'%')


	AND m.region_id IN

		#{regionId}



	AND m.enterprise_id IN

		#{enterpriseId}



	AND m.enterprise_id = #{enterpriseId}


	AND m.`material_status` = #{materialStatus}
