mapper.select:
SELECT a.id, a.enterprise_id AS enterpriseId, a.batch_no AS batchNo, a.quantity, a.prefix, a.applicant, a.apply_date AS applyDate, a.qr_code_status AS qrCodeStatus, a.reject_reason AS rejectReason, a.generate_man AS generateMan, a.generate_date AS generateDate, a.create_date AS createDate, is_generated AS isGenerated, u.realname AS applicantName, e.enterprise_name AS enterpriseName FROM qr_code_apply AS a LEFT JOIN sys_user AS u ON a.applicant = u.user_id LEFT JOIN enterprise AS e ON u.enterprise_id = e.id WHERE 1=1
AND a.batch_no LIKE concat('%',#{batchNo},'%')
AND a.enterprise_id = #{enterpriseId} OR a.enterprise_id IS NULL
order by ${sidx} ${order}
order by id desc
limit #{offset}, #{limit}
