mapper.select:
SELECT t.`id`, t.`name`, t.`remark`, t.`task_group_id`, t.`type`, t.`status`, t.`user_ids`, t.`user_names`, t.`start_time`, t.`end_time`, t.next_time, t.`schedule`, t.`schedule_cycle`, t.`create_time`, t.`update_time`, t.`data_status`, t.enterprise_id, t.region_id, tg.`name` AS taskGroupName, t.`delay_reason` AS delayReason, e.enterprise_name AS enterpriseName, r.`name` AS regionName FROM task AS t LEFT JOIN task_group AS tg ON t.task_group_id = tg.id LEFT JOIN enterprise AS e ON e.id = t.enterprise_id LEFT JOIN sys_region AS r ON r.id = t.region_id WHERE 1=1 AND t.data_status = 0
AND t.name LIKE concat('%',#{name},'%')
AND t.`task_group_id` = #{taskGroupId}
AND t.`status` = #{status}
AND t.`type` = #{type}
order by ${sidx} ${order}
order by id desc
limit #{offset}, #{limit}

select COUNT(1) FROM task AS t LEFT JOIN task_group AS tg ON t.task_group_id = tg.id LEFT JOIN enterprise AS e ON e.id = t.enterprise_id LEFT JOIN sys_region AS r ON r.id = t.region_id WHERE 1=1 AND t.data_status = 0
AND t.name LIKE concat('%',#{name},'%')
AND t.`task_group_id` = #{taskGroupId}
AND t.`status` = #{status}
AND t.`type` = #{type}

		select `id`, `name`, `remark`,`task_group_id`,`type`,`status`,`user_ids`,`user_names`,
    		`start_time`,`end_time`,next_time,`schedule`,`schedule_cycle`,`create_time`,`update_time`,`data_status`,`delay_reason`
		from task
		WHERE 1=1 AND data_status = 0

			AND name LIKE concat('%',#{name},'%')


			AND status =#{status}


			AND #{userId} = user_ids



                order by ${sidx} ${order}


                order by id desc



			limit #{offset}, #{limit}

SELECT region_id AS regionId, `status` AS `status`, COUNT(*) AS num FROM task WHERE 1 = 1
AND status =#{status}
AND #{startTime} <= `start_time` AND (`end_time`<= #{endTime} OR `end_time` IS NULL )
AND region_id IN
#{regionId}
GROUP BY region_id,`status`
