mapper.select:
SELECT r.id AS id, r.material_id AS materialId, r.task_group_id AS taskGroupId, g.`name` AS groupName, m.material_name AS materialName, m.qr_code AS qrCode, m.location AS location, m.material_status AS materialStatus, m.update_time AS updateTime, t.name AS materialTypeName FROM material AS m, material_type AS t, task_group_material AS r, task_group AS g WHERE m.id = r.material_id AND m.material_type_id = t.id AND r.task_group_id = g.id
AND m.material_name LIKE concat('%',#{materialName},'%')
AND r.task_group_id = #{taskGroupId}
AND t.id = #{typeId}
order by ${sidx} ${order}
order by id desc
limit #{offset}, #{limit}

SELECT COUNT(*) FROM material AS m, material_type AS t, task_group_material AS r, task_group AS g WHERE m.id = r.material_id AND m.material_type_id = t.id AND r.task_group_id = g.id
AND m.material_name LIKE concat('%',#{materialName},'%')
AND r.task_group_id = #{taskGroupId}
AND t.id = #{typeId}

select `id`, `task_group_id`, `material_id` from task_group_material WHERE 1=1
AND name LIKE concat('%',#{name},'%')
order by ${sidx} ${order}
order by id desc
limit #{offset}, #{limit}

mapper.delete:
delete from task_group_material where 1 = 1
 AND  `id` = #{id} 
 AND  `task_group_id` = #{taskGroupId} 
 AND `material_id` = #{materialId}
