mapper.select:
select
  u.*,
(SELECT r.`name` FROM sys_region AS r WHERE r.id = u.region_id) AS regionName,
(SELECT e.enterprise_name FROM enterprise AS e WHERE e.id = u.enterprise_id) AS enterpriseName,
 (select d.name from sys_dept d where d.dept_id = u.dept_id) deptName
from sys_user u


		and `create_user_id` = #{createUserId} 


		and u.`username` like concat('%',#{username},'%')


		AND region_id IN

			#{regionId}





		order by u.${sidx} ${order}


		order by u.user_id desc



	limit #{offset}, #{limit}

SELECT u.*, (SELECT r.`name` FROM sys_region AS r WHERE r.id = u.region_id) AS regionName, (SELECT e.enterprise_name FROM enterprise AS e WHERE e.id = u.enterprise_id) AS enterpriseName FROM sys_user u WHERE u.identify != 0
and `create_user_id` = #{createUserId}
and u.`username` like concat('%',#{username},'%')
AND region_id IN
#{regionId}
order by u.${sidx} ${order}
order by u.user_id desc
limit #{offset}, #{limit}

select count(1) from sys_user


		and `create_user_id` = #{createUserId} 


		and `username` like concat('%',#{username},'%') 


		and `status` = #{status}


		AND region_id IN

			#{regionId}

select count(1) from sys_user u where u.identify !=0

	and `create_user_id` = #{createUserId}


	and `username` like concat('%',#{username},'%')


	and `status` = #{status}


	AND region_id IN

		#{regionId}

select distinct rm.menu_id from sys_user_role ur 
	LEFT JOIN sys_role_menu rm on ur.role_id = rm.role_id 
where ur.user_id = #{userId}

mapper.delete:
delete from sys_user where user_id in 

	#{userId}

;
delete from sys_user_role where user_id in 

	#{userId}
